'use strict';

function createSelection() {
  const api = {
    each(callback) {
      if (typeof callback === 'function') {
        // no elements, so nothing to iterate
      }
      return api;
    },
    find() {
      return api;
    },
    text() {
      return '';
    },
    attr() {
      return undefined;
    }
  };
  return api;
}

function load() {
  const selector = () => createSelection();
  selector.find = () => createSelection();
  selector.each = (cb) => {
    const selection = createSelection();
    selection.each(cb);
    return selection;
  };
  selector.text = () => '';
  selector.attr = () => undefined;
  return selector;
}

module.exports = { load };
